import { db } from "./db.js";
import { adminUsers } from "../shared/schema.js";
import bcrypt from "bcryptjs";

async function seed() {
  const passwordHash = await bcrypt.hash("admin123", 10);

  await db.insert(adminUsers).values({
    username: "admin",
    email: "admin@africamechanize.org",
    passwordHash,
    fullName: "Default Admin",
    role: "super_admin",
  });

  console.log("‚úÖ Default admin user seeded.");
  process.exit(0);
}

seed().catch((err) => {
  console.error("‚ùå Seeding failed:", err);
  process.exit(1);
});

server/storage.js.content:
import {
  users,
  frameworkElements,
  newsletterSubscriptions,
  contactForms,
  webinars,
  webinarResources,
  webinarRecordings,
  webinarRegistrations,
  statistics,
  adminUsers,
  newsEvents,
  resources,
  legacyAdminAccounts,
  legacyResources,
  legacyWebinarAttendees,
  legacyProjects,
  legacyMembership,
} from "../shared/schema.js";
import { db } from "./db.js";
import { eq, sql, desc } from "drizzle-orm";

// Helper for normalizing date
function normalizeDate(value) {
  if (!value) return null;
  if (value instanceof Date) return value;

  const parsed = new Date(value);
  if (isNaN(parsed.getTime())) {
    throw new Error("Invalid scheduledDate format");
  }
  return parsed;
}

export class DatabaseStorage {
  // User methods
  async getUser(id) {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username) {
    const [user] = await db
      .select()
      .from(users)
      .where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser) {
    const [user] = await db.insert(users).values(insertUser).returning();
    return user;
  }

  async getUserByEmail(email) {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user || undefined;
  }

  async updateUser(id, updateData) {
    const [user] = await db
      .update(users)
      .set({ ...updateData, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  // Framework elements methods
  async getAllFrameworkElements() {
    return await db.select().from(frameworkElements);
  }

  async getFrameworkElement(id) {
    const [element] = await db
      .select()
      .from(frameworkElements)
      .where(eq(frameworkElements.id, id));
    return element || undefined;
  }

  async createFrameworkElement(insertFrameworkElement) {
    const [element] = await db
      .insert(frameworkElements)
      .values(insertFrameworkElement)
      .returning();
    return element;
  }

  // Legacy data methods for frontend integration
  async getLegacyResources() {
    try {
      return await db
        .select()
        .from(legacyResources)
        .orderBy(desc(legacyResources.id));
    } catch (error) {
      console.error("Error fetching legacy resources:", error);
      return [];
    }
  }

  async getLegacyProjects() {
    try {
      return await db
        .select()
        .from(legacyProjects)
        .orderBy(desc(legacyProjects.id));
    } catch (error) {
      console.error("Error fetching legacy projects:", error);
      return [];
    }
  }

  async getLegacyData() {
    try {
      const [resourceCount] = await db
        .select({ count: sql`count(*)` })
        .from(legacyResources);
      const [projectCount] = await db
        .select({ count: sql`count(*)` })
        .from(legacyProjects);
      const [memberCount] = await db
        .select({ count: sql`count(*)` })
        .from(legacyMembership);
      const [attendeeCount] = await db
        .select({ count: sql`count(*)` })
        .from(legacyWebinarAttendees);

      return {
        resourcesCount: resourceCount?.count || 0,
        projectsCount: projectCount?.count || 0,
        membersCount: memberCount?.count || 0,
        attendeesCount: attendeeCount?.count || 0,
        totalValue: 13000000, // $13M project portfolio value
        partnerships: ["FAO", "CGIAR", "ACT Africa", "Universities"],
        migrationComplete: true,
      };
    } catch (error) {
      console.error("Error fetching legacy data summary:", error);
      return {};
    }
  }

  // Events methods
  async getAllEvents() {
    return await db.select().from(newsEvents);
  }

  async getEvent(id) {
    const [event] = await db
      .select()
      .from(newsEvents)
      .where(eq(newsEvents.id, id));
    return event || undefined;
  }

  async createEvent(insertEvent) {
    const [event] = await db.insert(newsEvents).values(insertEvent).returning();
    return event;
  }

  async updateEvent(id, updateData) {
    const [event] = await db
      .update(events)
      .set(updateData)
      .where(eq(events.id, id))
      .returning();
    return event;
  }

  async deleteEvent(id) {
    await db.delete(events).where(eq(events.id, id));
  }

  // Resources methods
  async getAllResources() {
    return await db.select().from(resources);
  }

  async getResource(id) {
    const [resource] = await db
      .select()
      .from(resources)
      .where(eq(resources.id, id));
    return resource || undefined;
  }

  async getResourcesByCategory(category) {
    return await db
      .select()
      .from(resources)
      .where(eq(resources.category, category));
  }

  async createResource(insertResource) {
    const [resource] = await db
      .insert(resources)
      .values(insertResource)
      .returning();
    return resource;
  }

  async updateResource(id, updateData) {
    const [resource] = await db
      .update(resources)
      .set(updateData)
      .where(eq(resources.id, id))
      .returning();
    return resource;
  }

  async deleteResource(id) {
    await db.delete(resources).where(eq(resources.id, id));
  }

  // Newsletter methods
  async createNewsletter(insertNewsletter) {
    const [newsletter] = await db
      .insert(newsletterSubscriptions)
      .values(insertNewsletter)
      .returning();
    return newsletter;
  }

  // Contact methods
  async createContact(insertContact) {
    const [contact] = await db
      .insert(contactForms)
      .values(insertContact)
      .returning();
    return contact;
  }

  // Statistics methods
  async getStatistics() {
    const [stats] = await db.select().from(statistics);
    return stats || undefined;
  }

  async updateStatistics(insertStatistics) {
    const [stats] = await db
      .insert(statistics)
      .values(insertStatistics)
      .onConflictDoUpdate({
        target: statistics.id,
        set: insertStatistics,
      })
      .returning();
    return stats;
  }

  // Admin user methods
  async getAdminUser(username) {
    const [admin] = await db
      .select()
      .from(adminUsers)
      .where(eq(adminUsers.username, username));
    return admin || undefined;
  }

  async getAdminUserByEmail(email) {
    const [admin] = await db
      .select()
      .from(adminUsers)
      .where(eq(adminUsers.email, email));
    return admin || undefined;
  }

  async createAdminUser(insertAdminUser) {
    const [admin] = await db
      .insert(adminUsers)
      .values(insertAdminUser)
      .returning();
    return admin;
  }

  async updateAdminUser(id, updateData) {
    const [admin] = await db
      .update(adminUsers)
      .set({ ...updateData, updatedAt: new Date() })
      .where(eq(adminUsers.id, id))
      .returning();
    return admin;
  }

  async getAdminStats() {
    const [userCount] = await db.select({ count: sql`count(*)` }).from(users);
    const [webinarCount] = await db
      .select({ count: sql`count(*)` })
      .from(webinars)
      .where(eq(webinars.status, "upcoming"));
    const [contactCount] = await db
      .select({ count: sql`count(*)` })
      .from(contactForms)
      .where(eq(contactForms.status, "new"));
    const [newsletterCount] = await db
      .select({ count: sql`count(*)` })
      .from(newsletterSubscriptions)
      .where(eq(newsletterSubscriptions.isActive, true));

    return {
      totalUsers: parseInt(userCount.count) || 0,
      activeWebinars: parseInt(webinarCount.count) || 0,
      contactForms: parseInt(contactCount.count) || 0,
      newsletterSubs: parseInt(newsletterCount.count) || 0,
    };
  }

  async getAllUsers() {
    return await db
      .select()
      .from(users)
      .orderBy(sql`created_at DESC`);
  }

  async getAllContacts() {
    return await db
      .select()
      .from(contactForms)
      .orderBy(sql`created_at DESC`);
  }

  async deleteUser(id) {
    await db.delete(users).where(eq(users.id, id));
  }

  async updateContact(id, updateData) {
    const [contact] = await db
      .update(contactForms)
      .set({ ...updateData, updatedAt: new Date() })
      .where(eq(contactForms.id, id))
      .returning();
    return contact;
  }

  async updateUser(id, userData) {
    const [user] = await db
      .update(users)
      .set({ ...userData, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async deleteContact(id) {
    await db.delete(contactForms).where(eq(contactForms.id, id));
  }

  async createWebinar(insertWebinar) {
    if (!insertWebinar.scheduledDate) {
      throw new Error("scheduledDate is required");
    }

    let scheduledDate = insertWebinar.scheduledDate;
    if (!(scheduledDate instanceof Date)) {
      const parsed = new Date(scheduledDate);
      if (isNaN(parsed)) {
        throw new Error("Invalid scheduledDate format");
      }
      scheduledDate = parsed;
    }

    const [webinar] = await db
      .insert(webinars)
      .values({
        ...insertWebinar,
        scheduledDate,
        createdAt: new Date(),
        updatedAt: new Date(),
      })
      .returning();

    return webinar;
  }

  async updateWebinar(id, updateData) {
    // Clone updateData and set updatedAt timestamp
    const updatePayload = {
      ...updateData,
      updatedAt: new Date(),
    };

    // Validate and convert scheduledDate if present
    if (updatePayload.scheduledDate) {
      const dateObj = new Date(updatePayload.scheduledDate);
      if (isNaN(dateObj.getTime())) {
        throw new Error("Invalid scheduledDate");
      }
      updatePayload.scheduledDate = dateObj; // Convert to Date instance for ORM
    }

    // Perform the update and return the updated webinar
    const [webinar] = await db
      .update(webinars)
      .set(updatePayload)
      .where(eq(webinars.id, id))
      .returning();

    return webinar;
  }

  async deleteWebinar(id) {
    await db.delete(webinars).where(eq(webinars.id, id));
  }

  // Webinars methods
  async getAllWebinars() {
    return await db.select().from(webinars);
  }

  async getWebinar(id) {
    const [webinar] = await db
      .select()
      .from(webinars)
