client/src/pages/ResetPassword.jsx.content:
import { useEffect, useState } from 'react';
import { useLocation } from 'wouter';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { Eye, EyeOff, Lock, CheckCircle, XCircle, Loader2 } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from '@/components/ui/form';
import { useResetPassword } from '@/hooks/useAuth';
import { resetPasswordSchema } from '@shared/schema';
import { Link } from 'wouter';

export default function ResetPassword() {
  const [, setLocation] = useLocation();
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [resetComplete, setResetComplete] = useState(false);
  const [token, setToken] = useState('');

  const resetPasswordMutation = useResetPassword();

  const form = useForm({
    resolver: zodResolver(resetPasswordSchema),
    defaultValues: {
      password: '',
      confirmPassword: '',
      token: '',
    },
  });

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const resetToken = urlParams.get('token');

    if (resetToken) {
      setToken(resetToken);
      form.setValue('token', resetToken);
    } else {
      setLocation('/login');
    }
  }, [form, setLocation]);

  const onSubmit = async (data) => {
    try {
      await resetPasswordMutation.mutateAsync(data);
      setResetComplete(true);
      form.reset();
    } catch (error) {
      // Error is handled by the mutation's onError callback
    }
  };

  if (resetComplete) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-green-50 to-emerald-100 flex items-center justify-center p-4">
        <div className="w-full max-w-md">
          <div className="text-center mb-8">
            <h1 className="text-3xl font-bold text-green-800 mb-2">Africa Mechanize</h1>
            <p className="text-green-600">Sustainable Agricultural Mechanization</p>
          </div>

          <Card className="w-full">
            <CardHeader className="text-center space-y-4">
              <div className="mx-auto w-12 h-12 bg-green-100 rounded-full flex items-center justify-center">
                <CheckCircle className="h-6 w-6 text-green-600" />
              </div>
              <CardTitle className="text-2xl font-bold">Password Reset Complete!</CardTitle>
              <CardDescription>
                Your password has been successfully updated. You can now sign in with your new password.
              </CardDescription>
            </CardHeader>

            <CardContent>
              <div className="space-y-4">
                <div className="p-4 bg-green-50 rounded-lg border border-green-200">
                  <p className="text-sm text-green-800">
                    Your account is now secure with your new password. 
                    Please keep it safe and don't share it with anyone.
                  </p>
                </div>

                <Button 
                  className="w-full" 
                  onClick={() => setLocation('/login')}
                  data-testid="button-sign-in"
                >
                  Sign In Now
                </Button>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  if (!token) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-green-50 to-emerald-100 flex items-center justify-center p-4">
        <div className="w-full max-w-md">
          <Card className="w-full">
            <CardHeader className="text-center space-y-4">
              <div className="mx-auto w-12 h-12 bg-red-100 rounded-full flex items-center justify-center">
                <XCircle className="h-6 w-6 text-red-600" />
              </div>
              <CardTitle className="text-2xl font-bold">Invalid Reset Link</CardTitle>
              <CardDescription>
                This password reset link is invalid or has expired.
              </CardDescription>
            </CardHeader>

            <CardContent>
              <div className="space-y-4">
                <div className="p-4 bg-red-50 rounded-lg border border-red-200">
                  <p className="text-sm text-red-800">
                    Please request a new password reset link from the login page.
                  </p>
                </div>

                <Button 
                  className="w-full" 
                  onClick={() => setLocation('/login')}
                  data-testid="button-back-to-login"
                >
                  Back to Login
                </Button>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 to-emerald-100 flex items-center justify-center p-4">
      <div className="w-full max-w-md">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-green-800 mb-2">Africa Mechanize</h1>
          <p className="text-green-600">Sustainable Agricultural Mechanization</p>
        </div>

        <Card className="w-full max-w-md mx-auto">
          <CardHeader className="space-y-1">
            <CardTitle className="text-2xl font-bold text-center">Reset Your Password</CardTitle>
            <CardDescription className="text-center">
              Enter your new password below
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>New Password</FormLabel>
                      <FormControl>
                        <div className="relative">
                          <Lock className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                          <Input
                            {...field}
                            type={showPassword ? 'text' : 'password'}
                            placeholder="Enter new password"
                            className="pl-10 pr-10"
                            data-testid="input-new-password"
                          />
                          <Button
                            type="button"
                            variant="ghost"
                            size="sm"
                            className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                            onClick={() => setShowPassword(!showPassword)}
                            data-testid="button-toggle-password"
                          >
                            {showPassword ? (
                              <EyeOff className="h-4 w-4 text-muted-foreground" />
                            ) : (
                              <Eye className="h-4 w-4 text-muted-foreground" />
                            )}
                          </Button>
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="confirmPassword"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Confirm New Password</FormLabel>
                      <FormControl>
                        <div className="relative">
                          <Lock className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                          <Input
                            {...field}
                            type={showConfirmPassword ? 'text' : 'password'}
                            placeholder="Confirm new password"
                            className="pl-10 pr-10"
                            data-testid="input-confirm-password"
                          />
                          <Button
                            type="button"
                            variant="ghost"
                            size="sm"
                            className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                            onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                            data-testid="button-toggle-confirm-password"
                          >
                            {showConfirmPassword ? (
                              <EyeOff className="h-4 w-4 text-muted-foreground" />
                            ) : (
                              <Eye className="h-4 w-4 text-muted-foreground" />
                            )}
                          </Button>
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button
                  type="submit"
                  className="w-full"
                  disabled={resetPasswordMutation.isPending}
                  data-testid="button-reset-password"
                >
                  {resetPasswordMutation.isPending ? (
                    <>
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                      Resetting Password...
                    </>
                  ) : (
                    'Reset Password'
                  )}
                </Button>
              </form>
            </Form>

            <div className="mt-4 text-center text-sm">
              <span className="text-muted-foreground">Remember your password? </span>
              <Link href="/login">
                <Button variant="link" className="px-0 font-semibold" data-testid="link-login">
                  Sign In
                </Button>
              </Link>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/WebinarDetail.jsx.content:
import { useQuery } from "@tanstack/react-query";
import { useRoute } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { 
  Calendar, 
  Clock, 
  Users, 
  Play, 
  Download, 
  FileText, 
  Lock,
  Video,
  ExternalLink,
  ArrowLeft
} from "lucide-react";
import { format } from "date-fns";
import { Link } from "wouter";

export default function WebinarDetail() {
  const [match, params] = useRoute("/webinars/:slug");
  const { isAuthenticated, user } = useAuth();

  const { data: webinar, isLoading } = useQuery({
    queryKey: [`/api/webinars/${params?.slug}`],
    enabled: !!params?.slug
  });

  if (!match) {
    return <div>Webinar not found</div>;
  }

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        <div className="container mx-auto px-6 py-24">
          <div className="animate-pulse space-y-8">
            <div className="h-8 bg-gray-300 rounded w-3/4"></div>
            <div className="h-64 bg-gray-300 rounded"></div>
            <div className="space-y-4">
              <div className="h-4 bg-gray-300 rounded w-full"></div>
              <div className="h-4 bg-gray-300 rounded w-5/6"></div>
              <div className="h-4 bg-gray-300 rounded w-4/6"></div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (!webinar) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-gray-900 dark:text-gray-100 mb-4">
            Webinar Not Found
          </h1>
          <p className="text-gray-600 dark:text-gray-400 mb-8">
            The webinar you're looking for doesn't exist or has been removed.
          </p>
          <Button asChild>
            <Link href="/webinars">
              <ArrowLeft className="w-4 h-4 mr-2" />
              Back to Webinars
            </Link>
          </Button>
        </div>
      </div>
    );
  }

  const formatDate = (date) => format(new Date(date), "EEEE, MMMM dd, yyyy");
  const formatTime = (date) => format(new Date(date), "h:mm a");

  const getStatusColor = (status) => {
    switch (status) {
      case "upcoming": return "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200";
      case "live": return "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200";
      case "completed": return "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200";
      case "cancelled": return "bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200";
      default: return "bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200";
    }
  };

  const handleRegister = async () => {
    try {
      const response = await fetch(`/api/webinars/${webinar.slug}/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        // Refresh the data
        window.location.reload();
      }
    } catch (error) {
      console.error('Registration failed:', error);
    }
  };

  const handleResourceDownload = async (resourceId) => {
    try {
      const response = await fetch(`/api/webinars/${webinar.slug}/resources/${resourceId}/download`);
      const data = await response.json();

      if (response.ok && data.downloadUrl) {
        window.open(data.downloadUrl, '_blank');
      }
    } catch (error) {
      console.error('Download failed:', error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      {/* Hero Section */}
      <div className="relative">
        {webinar.bannerImage && (
          <div className="h-96 overflow-hidden">
            <img 
              src={webinar.bannerImage} 
              alt={webinar.title}
              className="w-full h-full object-cover"
            />
            <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/40 to-transparent" />
          </div>
        )}

        <div className="container mx-auto px-6 py-12 relative">
          <div className="max-w-4xl mx-auto">
            {/* Back Button */}
            <Button variant="ghost" size="sm" className="mb-6 text-gray-600 hover:text-gray-900" asChild>
              <Link href="/webinars">
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Webinars
              </Link>
            </Button>

            {/* Status and Category */}
            <div className="flex items-center space-x-2 mb-4">
              <Badge className={getStatusColor(webinar.status)}>
                {webinar.status === "upcoming" && "Upcoming"}
                {webinar.status === "live" && "Live Now"}
                {webinar.status === "completed" && "Completed"}
                {webinar.status === "cancelled" && "Cancelled"}
              </Badge>
              {webinar.category && (
                <Badge variant="outline">{webinar.category}</Badge>
              )}
              {webinar.tags?.map((tag, index) => (
                <Badge key={index} variant="secondary" className="text-xs">
                  {tag}
                </Badge>
              ))}
            </div>

            {/* Title and Description */}
            <h1 className="text-4xl md:text-5xl font-bold text-gray-900 dark:text-gray-100 mb-6">
              {webinar.title}
            </h1>

            <p className="text-xl text-gray-600 dark:text-gray-300 mb-8 leading-relaxed">
              {webinar.description}
            </p>

            {/* Date, Time, Duration */}
            <div className="flex flex-wrap items-center text-gray-600 dark:text-gray-400 space-x-6 mb-8">
              <div className="flex items-center">
                <Calendar className="w-5 h-5 mr-2" />
                {formatDate(webinar.scheduledDate)}
              </div>
              <div className="flex items-center">
                <Clock className="w-5 h-5 mr-2" />
                {formatTime(webinar.scheduledDate)}
              </div>
              <div className="flex items-center">
                <Clock className="w-5 h-5 mr-2" />
                {webinar.duration} minutes
              </div>
              {webinar.maxAttendees && (
                <div className="flex items-center">
                  <Users className="w-5 h-5 mr-2" />
                  {webinar.currentAttendees || 0} / {webinar.maxAttendees} attendees
                </div>
              )}
            </div>

            {/* Speaker Info */}
            <Card className="mb-8">
              <CardContent className="p-6">
                <div className="flex items-start space-x-4">
                  <Avatar className="h-16 w-16">
                    <AvatarImage src={webinar.speakerImage} alt={webinar.speakerName} />
                    <AvatarFallback>
                      {webinar.speakerName?.split(' ').map(n => n[0]).join('') || 'S'}
                    </AvatarFallback>
                  </Avatar>
                  <div className="flex-1">
                    <h3 className="text-xl font-semibold text-gray-900 dark:text-gray-100 mb-1">
                      {webinar.speakerName}
                    </h3>
                    {webinar.speakerTitle && (
                      <p className="text-gray-600 dark:text-gray-400 mb-3">
                        {webinar.speakerTitle}
                      </p>
                    )}
                    {webinar.speakerBio && (
                      <p className="text-gray-700 dark:text-gray-300 leading-relaxed">
                        {webinar.speakerBio}
                      </p>
                    )}
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Action Buttons */}
            <div className="flex flex-wrap gap-4 mb-12">
              {webinar.status === "upcoming" && webinar.registrationRequired && (
                <Button 
                  size="lg" 
                  onClick={handleRegister}
                  disabled={!isAuthenticated || webinar.isRegistered}
                  className="bg-primary hover:bg-primary/90"
                >
                  {!isAuthenticated ? (
                    <>
                      <Lock className="w-5 h-5 mr-2" />
                      Login to Register
                    </>
                  ) : webinar.isRegistered ? (
                    "Already Registered"
                  ) : (
                    "Register Now"
                  )}
                </Button>
              )}

              {webinar.status === "live" && (
                <Button size="lg" className="bg-red-600 hover:bg-red-700">
                  <Play className="w-5 h-5 mr-2" />
                  Join Live Session
                </Button>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Content Tabs */}
      <div className="container mx-auto px-6 pb-16">
        <div className="max-w-4xl mx-auto">
          <Tabs defaultValue="resources" className="w-full">
            <TabsList className="grid w-full grid-cols-3">
              <TabsTrigger value="resources">Resources & Downloads</TabsTrigger>
              <TabsTrigger value="recordings">Recordings</TabsTrigger>
              <TabsTrigger value="discussion">Discussion</TabsTrigger>
            </TabsList>

            {/* Resources Tab */}
            <TabsContent value="resources" className="mt-8">
              <div className="space-y-6">
                {/* Public Resources */}
                {webinar.resources?.public?.length > 0 && (
                  <Card>
                    <CardHeader>
                      <CardTitle>Presentation Materials</CardTitle>
                    </CardHeader>
                    <CardContent>
                      <div className="space-y-3">
                        {webinar.resources.public.map((resource) => (
                          <div key={resource.id} className="flex items-center justify-between p-4 border rounded-lg">
                            <div className="flex items-center space-x-3">
                              <FileText className="w-5 h-5 text-gray-400" />
                              <div>
                                <p className="font-medium text-gray-900 dark:text-gray-100">
                                  {resource.title}
                                </p>
                                {resource.description && (
                                  <p className="text-sm text-gray-500 dark:text-gray-400">
                                    {resource.description}
                                  </p>
                                )}
                              </div>
                            </div>
                            <Button 
                              variant="outline" 
                              size="sm"
                              onClick={() => handleResourceDownload(resource.id)}
                            >
                              <Download className="w-4 h-4 mr-2" />
                              Download
                            </Button>
                          </div>
                        ))}
                      </div>
                    </CardContent>
                  </Card>
                )}

                {/* Auth-Required Resources */}
                {isAuthenticated && webinar.resources?.authenticated?.length > 0 && (
                  <Card>
                    <CardHeader>
                      <CardTitle>Exclusive Resources</CardTitle>
                    </CardHeader>
                    <CardContent>
                      <div className="space-y-3">
                        {webinar.resources.authenticated.map((resource) => (
                          <div key={resource.id} className="flex items-center justify-between p-4 border rounded-lg">
                            <div className="flex items-center space-x-3">
                              <FileText className="w-5 h-5 text-primary" />
                              <div>
                                <p className="font-medium text-gray-900 dark:text-gray-100">
                                  {resource.title}
                                </p>
                                {resource.description && (
                                  <p className="text-sm text-gray-500 dark:text-gray-400">
                                    {resource.description}
                                  </p>
                                )}
                              </div>
                            </div>
                            <Button 
                              variant="outline" 
                              size="sm"
                              onClick={() => handleResourceDownload(resource.id)}
                            >
                              <Download className="w-4 h-4 mr-2" />
                              Download
                            </Button>
                          </div>
                        ))}
                      </div>
                    </CardContent>
                  </Card>
                )}

                {/* No Resources Message */}
                {(!webinar.resources?.public?.length && !webinar.resources?.authenticated?.length) && (
                  <Alert>
                    <FileText className="h-4 w-4" />
                    <AlertDescription>
                      Resources for this webinar will be available soon.
                    </AlertDescription>
                  </Alert>
                )}
              </div>
            </TabsContent>

            {/* Recordings Tab */}
            <TabsContent value="recordings" className="mt-8">
              <div className="space-y-6">
                {isAuthenticated ? (
                  webinar.recordings?.length > 0 ? (
                    <div className="space-y-4">
                      {webinar.recordings.map((recording) => (
                        <Card key={recording.id}>
                          <CardContent className="p-6">
                            <div className="flex items-start justify-between">
                              <div className="flex items-start space-x-4">
                                <div className="w-16 h-16 bg-gray-100 dark:bg-gray-800 rounded-lg flex items-center justify-center">
                                  <Video className="w-8 h-8 text-gray-400" />
                                </div>
                                <div className="flex-1">
                                  <h3 className="text-lg font-semibold text-gray-900 dark:text-gray-100 mb-2">
                                    {recording.title}
                                  </h3>
                                  {recording.description && (
                                    <p className="text-gray-600 dark:text-gray-400 mb-3">
                                      {recording.description}
                                    </p>
                                  )}
                                  <div className="flex items-center text-sm text-gray-500 dark:text-gray-400 space-x-4">
                                    <span>Duration: {recording.duration} min</span>
                                    <span>Views: {recording.viewCount || 0}</span>
                                  </div>
                                </div>
                              </div>
                              <Button onClick={() => window.open(recording.recordingUrl, '_blank')}>
                                <Play className="w-4 h-4 mr-2" />
                                Watch
                                <ExternalLink className="w-4 h-4 ml-2" />
                              </Button>
                            </div>
                          </CardContent>
                        </Card>
                      ))}
                    </div>
                  ) : (
                    <Alert>
                      <Video className="h-4 w-4" />
                      <AlertDescription>
                        Recordings for this webinar are not yet available.
                      </AlertDescription>
                    </Alert>
                  )
                ) : (
                  <Alert>
                    <Lock className="h-4 w-4" />
                    <AlertDescription>
                      Please <Link href="/login" className="text-primary hover:underline">log in</Link> to access webinar recordings.
                    </AlertDescription>
                  </Alert>
                )}
              </div>
            </TabsContent>

            {/* Discussion Tab */}
            <TabsContent value="discussion" className="mt-8">
              <Alert>
                <FileText className="h-4 w-4" />
                <AlertDescription>
                  Discussion papers and community feedback will be available here.
                </AlertDescription>
              </Alert>
            </TabsContent>
          </Tabs>
        </div>
      </div>
    </div>
  );
}

client/src/pages/WebinarPresentations.js.content:
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Download, Search, FileText, Video, Image, Calendar, Globe } from "lucide-react";
import { useState } from "react";
import { useAuth } from "@/hooks/useAuth";

export default function WebinarPresentations() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [selectedLanguage, setSelectedLanguage] = useState("all");
  const { isAuthenticated } = useAuth();

  const { data: webinars, isLoading } = useQuery({
    queryKey: ["/api/webinars"],
  });

  const { data: resources, isLoading: resourcesLoading } = useQuery({
    queryKey: ["/api/resources"],
  });

  const allResources = resources || [];
  const webinarResources = webinars?.flatMap(webinar => [
    ...(webinar.resources?.public || []),
    ...(isAuthenticated ? (webinar.resources?.authenticated || []) : [])
  ]) || [];

  const combinedResources = [
    ...allResources.map(r => ({ ...r, source: 'resource' })),
    ...webinarResources.map(r => ({ ...r, source: 'webinar' }))
  ];

  const filteredResources = combinedResources.filter(resource => {
    const matchesSearch = searchTerm === "" || 
      resource.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      resource.description?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      resource.category?.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesCategory = selectedCategory === "all" || resource.category === selectedCategory;
    const matchesLanguage = selectedLanguage === "all" || resource.language === selectedLanguage;

    return matchesSearch && matchesCategory && matchesLanguage;
  });

  const categories = [...new Set(combinedResources.map(r => r.category).filter(Boolean))];
  const languages = [...new Set(combinedResources.map(r => r.language).filter(Boolean))];

  const getResourceIcon = (type) => {
    switch (type?.toLowerCase()) {
      case 'video':
      case 'recording':
        return <Video className="w-5 h-5" />;
      case 'image':
      case 'photo':
        return <Image className="w-5 h-5" />;
      default:
        return <FileText className="w-5 h-5" />;
    }
  };

  const getResourceTypeColor = (type) => {
    switch (type?.toLowerCase()) {
      case 'video':
      case 'recording':
        return 'bg-red-500';
      case 'presentation':
        return 'bg-blue-500';
      case 'document':
      case 'pdf':
        return 'bg-green-500';
      case 'image':
        return 'bg-purple-500';
      default:
        return 'bg-gray-500';
    }
  };

  if (isLoading || resourcesLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-b from-gray-50 to-white dark:from-gray-900 dark:to-gray-800 pt-24">
        <div className="container mx-auto px-6 py-12">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {[...Array(9)].map((_, i) => (
              <div key={i} className="bg-white dark:bg-gray-800 rounded-xl p-6 animate-pulse">
                <div className="w-full h-32 bg-gray-200 dark:bg-gray-700 rounded-lg mb-4"></div>
                <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded mb-2"></div>
                <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-3/4 mb-4"></div>
                <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded w-24"></div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-white dark:from-gray-900 dark:to-gray-800 pt-24">
      {/* Header */}
      <section className="py-16 bg-gradient-to-r from-primary/10 to-secondary/10">
        <div className="container mx-auto px-6">
          <div className="max-w-4xl mx-auto text-center">
            <h1 className="text-4xl md:text-5xl font-bold mb-6 bg-gradient-to-r from-primary to-secondary bg-clip-text text-transparent">
              Webinar Presentations & Downloads
            </h1>
            <p className="text-xl text-gray-600 dark:text-gray-300 mb-8">
              Access presentations, documents, and resources from our F-SAMA webinar series. 
              Download materials to support your sustainable mechanization initiatives.
            </p>
          </div>
        </div>
      </section>

      {/* Filters */}
      <section className="py-8 border-b border-gray-200 dark:border-gray-700">
        <div className="container mx-auto px-6">
          <div className="flex flex-col lg:flex-row gap-4 items-center">
            <div className="relative flex-1 max-w-md">
              <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
              <Input
                placeholder="Search presentations..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10"
                data-testid="input-search-presentations"
              />
            </div>

            <div className="flex gap-4">
              <Select value={selectedCategory} onValueChange={setSelectedCategory}>
                <SelectTrigger className="w-48" data-testid="select-category">
                  <SelectValue placeholder="All Categories" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Categories</SelectItem>
                  {categories.map(category => (
                    <SelectItem key={category} value={category}>{category}</SelectItem>
                  ))}
                </SelectContent>
              </Select>

              <Select value={selectedLanguage} onValueChange={setSelectedLanguage}>
                <SelectTrigger className="w-40" data-testid="select-language">
                  <SelectValue placeholder="All Languages" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Languages</SelectItem>
                  {languages.map(language => (
                    <SelectItem key={language} value={language}>
                      <div className="flex items-center gap-2">
                        <Globe className="w-4 h-4" />
                        {language}
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        </div>
      </section>

      {/* Resources Grid */}
      <section className="py-16">
        <div className="container mx-auto px-6">
          {filteredResources.length > 0 ? (
            <>
              <div className="text-center mb-8">
                <p className="text-gray-600 dark:text-gray-300">
                  Showing {filteredResources.length} resource{filteredResources.length !== 1 ? 's' : ''}
                </p>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {filteredResources.map((resource, index) => (
                  <Card key={`${resource.source}-${resource.id || index}`} className="group hover:shadow-lg transition-all duration-300">
                    <CardHeader className="pb-4">
                      <div className="flex items-start justify-between mb-3">
                        <div className={`w-10 h-10 rounded-lg ${getResourceTypeColor(resource.type)} flex items-center justify-center text-white`}>
                          {getResourceIcon(resource.type)}
                        </div>
                        <div className="flex gap-2">
                          {resource.language && (
                            <Badge variant="secondary" className="text-xs">
                              <Globe className="w-3 h-3 mr-1" />
                              {resource.language}
                            </Badge>
                          )}
                          {resource.category && (
                            <Badge variant="outline" className="text-xs">
                              {resource.category}
                            </Badge>
                          )}
                        </div>
                      </div>

                      <CardTitle className="text-lg line-clamp-2 group-hover:text-primary transition-colors">
                        {resource.title}
                      </CardTitle>

                      <CardDescription className="line-clamp-3">
                        {resource.description}
                      </CardDescription>
                    </CardHeader>

                    <CardContent className="pt-0">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center text-sm text-gray-500 dark:text-gray-400">
                          <Calendar className="w-4 h-4 mr-1" />
                          {resource.createdAt ? new Date(resource.createdAt).toLocaleDateString() : 'N/A'}
                        </div>

                        {resource.downloadUrl ? (
                          <Button 
                            variant="outline" 
                            size="sm"
                            onClick={() => window.open(resource.downloadUrl, '_blank')}
                            data-testid={`button-download-${resource.id || index}`}
                          >
                            <Download className="w-4 h-4 mr-2" />
                            Download
                          </Button>
                        ) : (
                          <Button 
                            variant="outline" 
                            size="sm"
                            disabled
                          >
                            <FileText className="w-4 h-4 mr-2" />
                            View Details
                          </Button>
                        )}
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            </>
          ) : (
            <div className="text-center py-16">
              <FileText className="w-16 h-16 mx-auto mb-4 text-gray-400" />
              <h3 className="text-xl font-semibold mb-2">No Resources Found</h3>
              <p className="text-gray-600 dark:text-gray-300 mb-6">
                {searchTerm || selectedCategory !== "all" || selectedLanguage !== "all" 
                  ? "Try adjusting your search or filter criteria." 
                  : "No presentation resources are currently available."
                }
              </p>
              {(searchTerm || selectedCategory !== "all" || selectedLanguage !== "all") && (
                <Button 
                  variant="outline" 
                  onClick={() => {
                    setSearchTerm("");
                    setSelectedCategory("all");
                    setSelectedLanguage("all");
                  }}
                >
                  Clear Filters
                </Button>
              )}
            </div>
          )}
        </div>
      </section>
    </div>
  );
}

client/src/pages/Webinars.jsx.content:
import { useQuery } from "@tanstack/react-query";
import { WebinarCard } from "@/components/WebinarCard";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { Calendar, Clock, Users, Globe } from "lucide-react";
import { useApp } from "@/contexts/AppContext";

export default function Webinars() {
  const { t } = useApp();

  const { data: webinars, isLoading } = useQuery({
    queryKey: ["/api/webinars"],
  });

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-b from-gray-50 to-white dark:from-gray-900 dark:to-gray-800 pt-24">
        <div className="container mx-auto px-6 py-12">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {[...Array(6)].map((_, i) => (
              <div key={i} className="bg-white dark:bg-gray-800 rounded-xl p-6 animate-pulse">
                <div className="w-full h-48 bg-gray-200 dark:bg-gray-700 rounded-lg mb-4"></div>
                <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded mb-2"></div>
                <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-3/4 mb-4"></div>
                <div className="h-3 bg-gray-200 dark:bg-gray-700 rounded w-1/2"></div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  const upcomingWebinars = webinars?.filter(w => w.status === 'upcoming') || [];
  const recentWebinars = webinars?.filter(w => w.status === 'completed') || [];
  const ongoingWebinars = webinars?.filter(w => w.status === 'live') || [];

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-white dark:from-gray-900 dark:to-gray-800 pt-24">
      {/* Hero Section */}
      <section className="relative py-16 overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-r from-primary/10 to-secondary/10"></div>
        <div className="container mx-auto px-6 relative">
          <div className="max-w-4xl mx-auto text-center">
            <h1 className="text-4xl md:text-5xl font-bold mb-6 bg-gradient-to-r from-primary to-secondary bg-clip-text text-transparent">
              F-SAMA Webinar Series
            </h1>
            <p className="text-xl text-gray-600 dark:text-gray-300 mb-8 leading-relaxed">
              Join our comprehensive webinar series featuring international experts from FAO, CGIAR, 
              ACT Africa, and leading universities. Access current sessions plus archived presentations 
              from our established educational platform to advance sustainable mechanization practices.
            </p>
            <div className="flex flex-wrap justify-center gap-4 text-sm">
              <Badge variant="secondary" className="px-4 py-2">
                <Calendar className="w-4 h-4 mr-2" />
                Regular Series
              </Badge>
              <Badge variant="secondary" className="px-4 py-2">
                <Globe className="w-4 h-4 mr-2" />
                Multi-language
              </Badge>
              <Badge variant="secondary" className="px-4 py-2">
                <Users className="w-4 h-4 mr-2" />
                Expert Speakers
              </Badge>
              <Badge variant="secondary" className="px-4 py-2">
                <Clock className="w-4 h-4 mr-2" />
                2-3 Hours
              </Badge>
            </div>
          </div>
        </div>
      </section>

      {/* Webinars Content */}
      <section className="py-16">
        <div className="container mx-auto px-6">
          <Tabs defaultValue="all" className="w-full">
            <div className="flex justify-center mb-8">
              <TabsList className="grid w-full max-w-md grid-cols-3">
                <TabsTrigger value="all" data-testid="tab-all-webinars">All Webinars</TabsTrigger>
                <TabsTrigger value="upcoming" data-testid="tab-upcoming-webinars">
                  Upcoming ({upcomingWebinars.length})
                </TabsTrigger>
                <TabsTrigger value="past" data-testid="tab-past-webinars">
                  Past ({recentWebinars.length})
                </TabsTrigger>
              </TabsList>
            </div>

            <TabsContent value="all" className="space-y-8">
              {ongoingWebinars.length > 0 && (
                <div>
                  <h2 className="text-2xl font-bold mb-6 text-center text-red-600 dark:text-red-400">
                    üî¥ Live Now
                  </h2>
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 mb-12">
                    {ongoingWebinars.map((webinar) => (
                      <WebinarCard key={webinar.id} webinar={webinar} />
                    ))}
                  </div>
                </div>
              )}

              {upcomingWebinars.length > 0 && (
                <div>
                  <h2 className="text-2xl font-bold mb-6 text-center">Upcoming Webinars</h2>
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 mb-12">
                    {upcomingWebinars.map((webinar) => (
                      <WebinarCard key={webinar.id} webinar={webinar} />
                    ))}
                  </div>
                </div>
              )}

              {recentWebinars.length > 0 && (
                <div>
                  <h2 className="text-2xl font-bold mb-6 text-center">Past Webinars</h2>
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                    {recentWebinars.map((webinar) => (
                      <WebinarCard key={webinar.id} webinar={webinar} />
                    ))}
                  </div>
                </div>
              )}
            </TabsContent>

            <TabsContent value="upcoming">
              {upcomingWebinars.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                  {upcomingWebinars.map((webinar) => (
