// Admin guard component
const AdminGuard = ({ children }) => {
  const [isAuthorized, setIsAuthorized] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem("adminToken");
    if (!token) {
      window.location.href = "/admin/login";
      return;
    }

    // Verify token with backend
    fetch("/api/admin/verify", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => {
        if (res.ok) {
          setIsAuthorized(true);
        } else {
          localStorage.removeItem("adminToken");
          window.location.href = "/admin/login";
        }
      })
      .catch(() => {
        localStorage.removeItem("adminToken");
        window.location.href = "/admin/login";
      })
      .finally(() => setIsLoading(false));
  }, []);

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <Shield className="w-8 h-8 animate-spin mx-auto mb-4" />
          <p>Verifying access...</p>
        </div>
      </div>
    );
  }

  return isAuthorized ? children : null;
};

export default function AdminDashboard() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedUser, setSelectedUser] = useState(null);
  const [editingUser, setEditingUser] = useState(null);
  const [editingWebinar, setEditingWebinar] = useState(null);
  const [statsFormData, setStatsFormData] = useState({});
  const [contactFilter, setContactFilter] = useState("all");
  const [userSearch, setUserSearch] = useState("");

  // Fetch legacy data for admin management
  const { data: legacyData } = useQuery({
    queryKey: ["/api/legacy/data"],
    queryFn: async () => {
      const response = await fetch("/api/legacy/data");
      if (!response.ok) throw new Error("Failed to fetch legacy data");
      return response.json();
    },
  });

  // Content Management States
  const [activeTab, setActiveTab] = useState("dashboard");
  const [isCreateNewsEventOpen, setIsCreateNewsEventOpen] = useState(false);
  const [isEditNewsEventOpen, setIsEditNewsEventOpen] = useState(false);
  const [editingNewsEvent, setEditingNewsEvent] = useState(null);
  const [isCreateResourceOpen, setIsCreateResourceOpen] = useState(false);
  const [isEditResourceOpen, setIsEditResourceOpen] = useState(false);
  const [editingResource, setEditingResource] = useState(null);
  const [isCreateWebinarResourceOpen, setIsCreateWebinarResourceOpen] =
    useState(false);
  const [isEditWebinarResourceOpen, setIsEditWebinarResourceOpen] =
    useState(false);
  const [editingWebinarResource, setEditingWebinarResource] = useState(null);
  const [newWebinarData, setNewWebinarData] = useState({
    title: "",
    description: "",
    date: "",
    time: "",
    speaker: "",
    meetingLink: "",
  });

  // Form state for Resources
  const [resourceFormData, setResourceFormData] = useState({
    title: "",
    description: "",
    resourceType: "",
    fileUrl: "",
    category: "",
    author: "",
  });

  // Form state for News/Events
  const [newsEventFormData, setNewsEventFormData] = useState({
    title: "",
    description: "",
    eventType: "news",
    eventDate: "",
    eventLocation: "",
    eventOrganizer: "",
    content: "",
  });

  // Form state for Webinar Resources
  const [webinarResourceFormData, setWebinarResourceFormData] = useState({
    webinarId: "",
    title: "",
    resourceType: "",
    fileUrl: "",
    description: "",
  });

  const getAuthHeaders = () => {
    const token = localStorage.getItem("adminToken");
    return {
      Authorization: `Bearer ${token}`,
    };
  };

  const { data: stats, refetch: refetchStats } = useQuery({
    queryKey: ["/api/admin/stats"],
    queryFn: async () => {
      const response = await fetch("/api/admin/stats", {
        headers: getAuthHeaders(),
      });
      if (!response.ok) throw new Error("Failed to fetch stats");
      return response.json();
    },
  });

  const { data: users, refetch: refetchUsers } = useQuery({
    queryKey: ["/api/admin/users"],
    queryFn: async () => {
      const response = await fetch("/api/admin/users", {
        headers: getAuthHeaders(),
      });
      if (!response.ok) throw new Error("Failed to fetch users");
      return response.json();
    },
  });

  const { data: webinars, refetch: refetchWebinars } = useQuery({
    queryKey: ["/api/admin/webinars"],
    queryFn: async () => {
      const response = await fetch("/api/admin/webinars", {
        headers: getAuthHeaders(),
      });
      if (!response.ok) throw new Error("Failed to fetch webinars");
      return response.json();
    },
  });

  // Migration status query (avoiding duplicate legacyData variable)
  const { data: migrationStatus, refetch: refetchMigrationStatus } = useQuery({
    queryKey: ["/api/migration/status"],
    queryFn: async () => {
      const response = await fetch("/api/migration/status", {
        headers: getAuthHeaders(),
      });
      if (!response.ok) throw new Error("Failed to fetch migration status");
      return response.json();
    },
  });

  // Content Management Queries
  const { data: newsEvents, refetch: refetchNewsEvents } = useQuery({
    queryKey: ["/api/admin/news"],
    queryFn: async () => {
      const response = await fetch("/api/admin/news", {
        headers: getAuthHeaders(),
      });
      if (!response.ok) throw new Error("Failed to fetch news events");
      return response.json();
    },
  });

  const { data: resources, refetch: refetchResources } = useQuery({
    queryKey: ["/api/admin/resources"],
    queryFn: async () => {
      const response = await fetch("/api/admin/resources", {
        headers: getAuthHeaders(),
      });
      if (!response.ok) throw new Error("Failed to fetch resources");
      return response.json();
    },
  });

  const { data: webinarResources, refetch: refetchWebinarResources } = useQuery(
    {
      queryKey: ["/api/admin/webinar-resources"],
      queryFn: async () => {
        const response = await fetch("/api/admin/webinar-resources", {
          headers: getAuthHeaders(),
        });
        if (!response.ok) throw new Error("Failed to fetch webinar resources");
        return response.json();
      },
