      toast({ title: "User deleted successfully" });
      refetchUsers();
    },
    onError: (error) => {
      toast({
        title: "Delete failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const updateStatsFormMutation = useMutation({
    mutationFn: async (statsData) => {
      const response = await apiRequest("POST", "/api/statistics", statsData);
      return response;
