            Learn More
          </Link>
        </div>
      </div>

      <header
        className={`fixed top-6 w-full z-40 transition-all duration-300 ${
          isScrolled
            ? "bg-white/95 dark:bg-gray-900/95 backdrop-blur-md shadow-xl border-b border-gray-200/50 dark:border-gray-700/50"
            : "bg-white/80 dark:bg-gray-900/80 backdrop-blur-sm shadow-lg"
        }`}
      >
        <nav className="container mx-auto px-4 py-3">
          <div className="flex items-center justify-between">
            {/* Compact Logo */}
            <Link href="/" className="flex items-center space-x-2 group">
              <div className="w-10 h-10 bg-gradient-to-br from-primary to-secondary rounded-lg flex items-center justify-center group-hover:scale-105 transition-all duration-300">
                <Sprout className="text-white" size={20} />
              </div>
              <div className="hidden sm:block">
                <h1 className="text-lg font-bold bg-gradient-to-r from-primary to-secondary bg-clip-text text-transparent">
                  AfricaMechanize
                </h1>
              </div>
            </Link>

            {/* Compact Desktop Navigation */}
            <div className="hidden lg:flex items-center space-x-1">
              {navItems.map((item) => {
                const isActive =
                  location === item.href ||
                  (item.dropdown &&
                    item.dropdown.some((sub) => location === sub.href));

                if (item.dropdown) {
                  return (
                    <DropdownMenu key={item.label}>
                      <DropdownMenuTrigger asChild>
                        <Button
                          variant="ghost"
                          size="sm"
                          className={`font-medium ${
                            isActive
                              ? "text-primary"
                              : "text-gray-700 dark:text-gray-300 hover:text-primary"
                          }`}
                        >
                          {item.label}
                          <ChevronDown className="ml-1 h-3 w-3" />
                        </Button>
                      </DropdownMenuTrigger>
                      <DropdownMenuContent align="start" className="w-56">
                        {item.dropdown.map((dropdownItem) => (
                          <DropdownMenuItem key={dropdownItem.href} asChild>
                            <Link
                              href={dropdownItem.href}
                              className="cursor-pointer"
                            >
                              {dropdownItem.label}
                            </Link>
                          </DropdownMenuItem>
                        ))}
                      </DropdownMenuContent>
                    </DropdownMenu>
                  );
                }

                return (
                  <Link
                    key={item.href}
                    href={item.href}
                    className={`px-3 py-2 text-sm font-medium transition-colors rounded-md ${
                      isActive
                        ? "text-primary bg-primary/10"
                        : "text-gray-700 dark:text-gray-300 hover:text-primary hover:bg-primary/5"
                    }`}
                  >
                    {item.label}
                  </Link>
                );
              })}
            </div>

            {/* Compact Controls */}
            <div className="flex items-center space-x-1">
              {/* Search - Desktop only */}
              <Button
                variant="ghost"
                size="sm"
                className="hidden xl:flex"
                onClick={() => (window.location.href = "/resources")}
                data-testid="button-search"
              >
                <Search className="w-4 h-4" />
              </Button>

              <LanguageToggle />
              <ThemeToggle />

              {/* Authentication */}
              {!isLoading && (
                <div className="hidden lg:flex items-center space-x-1">
                  {isAuthenticated ? (
                    <UserMenu />
                  ) : (
                    <>
                      <Button
                        variant="ghost"
                        size="sm"
                        asChild
                        data-testid="button-header-login"
                      >
                        <Link href="/login">
                          <LogIn className="w-4 h-4 lg:mr-1" />
                          <span className="hidden xl:inline">Sign In</span>
                        </Link>
                      </Button>
                      <Button
                        size="sm"
                        asChild
                        data-testid="button-header-register"
                      >
                        <Link href="/register">
                          <UserPlus className="w-4 h-4 lg:mr-1" />
                          <span className="hidden xl:inline">Join</span>
                        </Link>
                      </Button>
                    </>
                  )}
                </div>
              )}

              {/* Mobile Menu Toggle */}
              <button
                onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
                className="lg:hidden p-2 rounded-lg hover:bg-gray-200 dark:hover:bg-gray-700 transition-colors"
                data-testid="button-mobile-menu"
              >
                {mobileMenuOpen ? <X size={18} /> : <Menu size={18} />}
              </button>
            </div>
          </div>

          {/* Improved Mobile Menu */}
          {mobileMenuOpen && (
            <div className="lg:hidden mt-4 pb-4 border-t border-gray-200 dark:border-gray-700">
              <div className="max-h-[70vh] overflow-y-auto">
                {/* Mobile Search */}
                <div className="p-4 border-b border-gray-200 dark:border-gray-700">
                  <Link
                    href="/resources"
                    onClick={() => setMobileMenuOpen(false)}
                    className="flex items-center gap-2 p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-800 transition-colors"
                  >
                    <Search className="w-4 h-4 text-gray-500" />
                    <span className="text-gray-700 dark:text-gray-300">
                      Search Resources
                    </span>
                  </Link>
                </div>

                <div className="p-4 space-y-1">
                  {navItems.map((item) => {
                    const isActive =
                      location === item.href ||
                      (item.dropdown &&
                        item.dropdown.some((sub) => location === sub.href));

                    if (item.dropdown) {
                      return (
                        <div key={item.label} className="space-y-1">
                          <div
                            className={`p-3 rounded-lg font-medium ${
                              isActive
                                ? "bg-primary/10 text-primary"
                                : "text-gray-700 dark:text-gray-300"
                            }`}
                          >
                            {item.label}
                          </div>
                          <div className="ml-4 space-y-1">
                            {item.dropdown.map((subItem) => (
                              <Link
                                key={subItem.href}
                                href={subItem.href}
                                className={`block p-2 rounded-md text-sm transition-colors ${
                                  location === subItem.href
                                    ? "bg-primary/10 text-primary font-medium"
                                    : "text-gray-600 dark:text-gray-400 hover:text-primary hover:bg-primary/5"
                                }`}
                                onClick={() => setMobileMenuOpen(false)}
                              >
                                {subItem.label}
                              </Link>
                            ))}
                          </div>
                        </div>
                      );
                    }

                    return (
                      <Link
                        key={item.href}
                        href={item.href}
                        className={`block p-3 rounded-lg font-medium transition-colors ${
                          isActive
                            ? "bg-primary/10 text-primary"
                            : "text-gray-700 dark:text-gray-300 hover:text-primary hover:bg-primary/5"
                        }`}
                        onClick={() => setMobileMenuOpen(false)}
                      >
                        {item.label}
                      </Link>
                    );
                  })}

                  {/* Mobile Authentication */}
                  {!isLoading && (
                    <div className="border-t border-gray-200 dark:border-gray-700 pt-4 mt-4 space-y-2">
                      {isAuthenticated ? (
                        <div className="space-y-2">
                          <div className="p-3 bg-primary/5 rounded-lg">
                            <div className="font-medium text-sm">
                              {user?.firstName || user?.email}
                            </div>
                          </div>
                          <Link
                            href="/profile"
                            onClick={() => setMobileMenuOpen(false)}
                            className="block p-2 rounded-lg text-gray-700 dark:text-gray-300 hover:text-primary hover:bg-primary/5 transition-colors"
                            data-testid="mobile-link-profile"
                          >
                            Profile
                          </Link>
                        </div>
                      ) : (
                        <div className="space-y-2">
                          <Link
                            href="/login"
                            onClick={() => setMobileMenuOpen(false)}
                            className="block p-3 text-center bg-primary text-white rounded-lg font-medium"
                            data-testid="mobile-button-login"
                          >
                            Sign In
                          </Link>
                          <Link
                            href="/register"
                            onClick={() => setMobileMenuOpen(false)}
                            className="block p-3 text-center border border-primary text-primary rounded-lg font-medium"
                            data-testid="mobile-button-register"
                          >
                            Join Now
                          </Link>
                        </div>
                      )}
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}
        </nav>
      </header>
    </>
  );
};

client/src/components/Hero.jsx.content:
import { useState, useEffect } from "react";
import { ChevronDown, Play, Sparkles, Leaf, Tractor } from "lucide-react";
import { Button } from "@/components/ui/button";

export const Hero = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [particles, setParticles] = useState([]);

  useEffect(() => {
    setIsVisible(true);

    // Create floating particles
    const newParticles = [];
    for (let i = 0; i < 20; i++) {
      newParticles.push({
        id: i,
        x: Math.random() * 100,
        y: Math.random() * 100,
        size: Math.random() * 4 + 2,
        delay: Math.random() * 2,
        duration: Math.random() * 3 + 2,
      });
    }
    setParticles(newParticles);
  }, []);

  const scrollToFramework = () => {
    const element = document.getElementById("framework");
    if (element) {
      element.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <section className="relative min-h-screen flex items-center justify-center overflow-hidden">
      {/* Hero Background with Gradient Overlay */}
      <div 
        className="absolute inset-0 bg-cover bg-center bg-fixed"
        style={{
          backgroundImage: `url('https://images.unsplash.com/photo-1586771107445-d3ca888129ff?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1920&h=1080')`
        }}
      >
        <div className="absolute inset-0 bg-gradient-to-br from-primary/80 via-black/60 to-secondary/80"></div>
      </div>

      {/* Floating Particles */}
      <div className="absolute inset-0 overflow-hidden">
        {particles.map((particle) => (
          <div
            key={particle.id}
            className="absolute w-2 h-2 bg-white/20 rounded-full animate-pulse"
            style={{
              left: `${particle.x}%`,
              top: `${particle.y}%`,
              animationDelay: `${particle.delay}s`,
              animationDuration: `${particle.duration}s`,
            }}
          />
        ))}
      </div>

      {/* Geometric Shapes */}
      <div className="absolute inset-0">
        <div className="absolute top-20 left-10 w-20 h-20 border-2 border-white/20 rounded-full animate-spin" style={{ animationDuration: '20s' }} />
        <div className="absolute top-40 right-20 w-16 h-16 border-2 border-secondary/30 transform rotate-45 animate-pulse" />
        <div className="absolute bottom-32 left-20 w-12 h-12 bg-white/10 rounded-full animate-bounce" style={{ animationDelay: '1s' }} />
        <div className="absolute bottom-20 right-32 w-8 h-8 bg-primary/20 transform rotate-45 animate-pulse" style={{ animationDelay: '2s' }} />
      </div>

      {/* Hero Content */}
      <div className="relative z-10 text-center text-white px-6 max-w-6xl mx-auto">
        {/* Floating Icons */}
        <div className="absolute -top-20 left-0 right-0 flex justify-center space-x-8">
          <div className="animate-float" style={{ animationDelay: '0s' }}>
            <Leaf className="text-primary w-8 h-8 opacity-60" />
          </div>
          <div className="animate-float" style={{ animationDelay: '1s' }}>
            <Tractor className="text-secondary w-10 h-10 opacity-70" />
          </div>
          <div className="animate-float" style={{ animationDelay: '2s' }}>
            <Sparkles className="text-accent w-6 h-6 opacity-50" />
          </div>
        </div>

        <div className="mb-8">
          <div className="inline-block px-4 py-2 bg-white/10 backdrop-blur-sm rounded-full border border-white/20 mb-6">
            <p className="text-white/90 text-sm font-medium">
              ðŸŒ± Sustainable Agricultural Mechanization Framework
            </p>
          </div>
        </div>

        <h1 
          className={`text-4xl sm:text-5xl md:text-6xl lg:text-7xl font-bold mb-6 transition-all duration-1000 ${
            isVisible ? "opacity-100 translate-y-0" : "opacity-0 translate-y-10"
          }`}
        >
          <span className="block bg-gradient-to-r from-white to-white/80 bg-clip-text text-transparent">
            Transforming
          </span>
          <span className="block bg-gradient-to-r from-secondary to-warning bg-clip-text text-transparent sm:inline sm:ml-4">
            Agriculture
          </span>
          <span className="block bg-gradient-to-r from-white to-white/80 bg-clip-text text-transparent">
            in Africa
          </span>
        </h1>

        <p 
          className={`text-lg sm:text-xl md:text-2xl mb-8 opacity-90 max-w-4xl mx-auto transition-all duration-1000 delay-300 leading-relaxed ${
            isVisible ? "opacity-90 translate-y-0" : "opacity-0 translate-y-10"
          }`}
        >
          Empowering farmers through innovative mechanization solutions, sustainable practices, and collaborative networks across the continent
        </p>

        <div 
          className={`flex flex-col sm:flex-row gap-4 justify-center mb-16 transition-all duration-1000 delay-500 ${
            isVisible ? "opacity-100 translate-y-0" : "opacity-0 translate-y-10"
          }`}
        >
          <Button
            size="lg"
            onClick={scrollToFramework}
            className="bg-gradient-to-r from-primary to-primary/90 hover:from-primary/90 hover:to-primary text-white px-8 py-4 rounded-full font-semibold transition-all duration-300 transform hover:scale-105 shadow-2xl hover:shadow-primary/25"
          >
            <Sparkles className="mr-2 w-5 h-5" />
            Explore F-SAMA Framework
          </Button>
          <Button
            size="lg"
            variant="outline"
            className="border-2 border-white/50 text-white hover:bg-white/20 hover:border-white backdrop-blur-sm px-8 py-4 rounded-full font-semibold transition-all duration-300 hover:scale-105"
          >
            <Play className="mr-2 w-5 h-5" />
            Watch Introduction
          </Button>
        </div>

        {/* Feature Pills */}
        <div className={`flex flex-wrap gap-3 justify-center mb-12 transition-all duration-1000 delay-700 ${
          isVisible ? "opacity-100 translate-y-0" : "opacity-0 translate-y-10"
        }`}>
          {["10 Key Elements", "25+ Countries", "Expert Network", "Sustainable Future"].map((pill, index) => (
            <div 
              key={pill}
              className="px-4 py-2 bg-white/10 backdrop-blur-sm rounded-full border border-white/20 text-white/90 text-sm font-medium hover:bg-white/20 transition-all duration-300 cursor-pointer"
              style={{ animationDelay: `${index * 0.1}s` }}
            >
              {pill}
            </div>
          ))}
        </div>
      </div>

      {/* Enhanced Scroll Indicator */}
      <div 
        className={`absolute bottom-8 left-1/2 transform -translate-x-1/2 cursor-pointer transition-all duration-1000 delay-700 group ${
          isVisible ? "opacity-100 translate-y-0" : "opacity-0 translate-y-10"
        }`}
        onClick={scrollToFramework}
      >
        <div className="flex flex-col items-center space-y-2">
          <div className="w-6 h-10 border-2 border-white/60 rounded-full flex justify-center group-hover:border-white transition-colors duration-300">
            <div className="w-1 h-3 bg-white/80 rounded-full mt-2 animate-pulse group-hover:bg-white transition-colors duration-300"></div>
          </div>
          <ChevronDown className="text-white/80 group-hover:text-white transition-colors duration-300 animate-bounce" size={24} />
          <p className="text-white/60 text-xs font-medium group-hover:text-white/80 transition-colors duration-300">
            Scroll to explore
          </p>
        </div>
      </div>
    </section>
  );
};

client/src/components/LanguageToggle.jsx.content:
import { useApp } from "../contexts/AppContext";

export const LanguageToggle = () => {
  const { language, setLanguage } = useApp();

  return (
    <div className="hidden md:flex items-center space-x-2">
      <button
        onClick={() => setLanguage("en")}
        className={`px-3 py-1 rounded text-sm font-medium transition-all duration-300 ${
          language === "en"
            ? "text-primary bg-primary/10"
            : "text-gray-600 dark:text-gray-400 hover:text-primary"
        }`}
      >
        EN
      </button>
      <span className="text-gray-400">|</span>
      <button
        onClick={() => setLanguage("fr")}
        className={`px-3 py-1 rounded text-sm font-medium transition-all duration-300 ${
          language === "fr"
            ? "text-primary bg-primary/10"
            : "text-gray-600 dark:text-gray-400 hover:text-primary"
        }`}
      >
        FR
      </button>
    </div>
  );
};

