import nodemailer from 'nodemailer';

// Email configuration
let transporter;

if (process.env.SMTP_HOST && process.env.SMTP_USER && process.env.SMTP_PASS) {
  // Custom SMTP configuration
  transporter = nodemailer.createTransport({
    host: process.env.SMTP_HOST,
    port: process.env.SMTP_PORT || 587,
    secure: process.env.SMTP_SECURE === 'true',
    auth: {
      user: process.env.SMTP_USER,
      pass: process.env.SMTP_PASS,
    },
  });
  console.log('ðŸ“§ Email service configured with custom SMTP');
} else {
  // No email configuration - disable email functionality
  console.log('âš ï¸  No SMTP credentials configured. Email functionality disabled.');
  transporter = null;
}

export async function sendEmail({ to, subject, text, html }) {
  try {
    if (!transporter) {
      console.log('ðŸ“§ Email not sent - SMTP not configured:', { to, subject });
      return false;
    }

    const mailOptions = {
      from: process.env.FROM_EMAIL || 'noreply@africamechanize.org',
      to,
      subject,
      text: text || html?.replace(/<[^>]*>/g, ''), // Strip HTML for text version
      html,
    };

    const info = await transporter.sendMail(mailOptions);

    console.log('ðŸ“§ Email sent successfully:', {
      to,
      subject,
      messageId: info.messageId,
    });

    return true;
  } catch (error) {
    console.error('ðŸ“§ Email send error:', error.message);
    return false;
  }
}

